/*
\    /\
 )  ( ')
(  /  )
 \(__)|
 */

public class Cat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("\\    /\\");
		System.out.println(" )  ( ')");
		System.out.println("(  /  )");
		System.out.println(" \\(__)|");
		// '\'는 앞에 '\'를 하나 더 붙여서 기능 취소.
	}

}
